/* Estilos generales */ 

*{
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
}
body {
  font-weight: 400;
  color: #020F14;
	line-height: 1.5;
	background-color: #ffffff;
}
header,footer,nav{
  /*font-family: 'Montserrat', sans-serif;
  */
}

h1,h2,h3,h4 {   
    font-weight: bold;
    color:#353d92;
}

p {
    font-size: 1.1rem;
    margin-bottom: 1rem;
}

b{
	font-weight: bold;
}

em{
	font-style: italic;
}

a {
    color: #353d92;
    text-decoration: none;
}

a:hover {
    color: #8cb2d3;
}

cite {
font-style: italic;
}
abbr {
    text-decoration: none;
}

h1 {
    font-size: 2.5rem;
    margin-left: 10%;
    margin-bottom: 10px;
}


h2 {
    font-size: 2rem;
    margin-bottom: 1.5rem;
}

h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
}

h4{
	font-size: 1.4rem;
	margin-bottom: 1rem;
}

/*Clase para esconder un elemento que será visible únicamente para los lectores de pantalla (aplicado para mejorar la accesibilidad)*/
.sr {
    position:absolute;
    height:1px;    
    width:1px;    
    overflow:hidden;    
    clip:rect(1px, 1px, 1px, 1px);
}
/****************************
Contenedores del sitio
*****************************/
header {
    padding-top: 10px;
	padding-bottom: 15px;
  margin-bottom: 10px;
  border-bottom: #8cb2d3 2px solid;
}

footer {
    padding-top: 15px;
    padding-bottom: 15px;    
    background-color: #8cb2d3;
    color: #FFFFFF;
}

main {
    padding-top: 10px;
    padding-bottom: 50px;
}

/*****Queries container*****/
.container {
	display: grid;
	grid-template-columns: 1fr;
    margin-right: auto;
    margin-left: auto;
    padding-right: 15px;
    padding-left: 15px;
	text-align: center;
}

@media (min-width: 370px) {
    .container {
      max-width: 360px;
    }
}

@media (min-width: 576px) {
    .container {
      max-width: 540px;
    }
}
  
@media (min-width: 768px) {
    .container {
      max-width: 720px;
    }
}
  
@media (min-width: 992px) {
    .container {
      max-width: 960px;
    }
}
  
@media (min-width: 1200px) {
    .container {
      max-width: 1140px;
    }
}

/**ISOLOGO**/
p.isologo {
	margin-top: 5px;
	margin-bottom: 10px;
  font-size: 35px;
}

p.isologo a {
    color: inherit;
	border: none;
	font-weight: bold;
	font-family: 'Merriweather', sans-serif;
}

p.isologo a:hover {
    color:#353d92;
}

/********
HEADER
*********/
header {
    text-align: center;
}
/* Menú de navegación*/
nav ul li {
    text-transform: uppercase;
    font-size: 1.1rem;
    font-weight: 400;
	padding-left: 0;
	list-style: none;
}

nav ul li a {
    color: #8cb2d3;
    border: none;
}

nav ul li a:hover {
    border: none;
    color: #353d92;
}

nav li a.activo {
	font-weight: bold;
	font-style: italic;
}

nav li a.activo::before {
    font-weight: 400;
}

@media (min-width: 576px) {
    header.container {
		display: flex;
		flex-direction: column;
	  }
}
  
@media (min-width: 768px) {
    header {
		display: flex;
		flex-direction: column;
	  }
}
 
@media (min-width: 992px) {
	header div.container {
		display: grid;
		grid-template-columns: 1fr 1fr 1fr;
		width: 100%;
	}
	header li{
		display: inline;
		margin: 5px;
	}
	.logo{
		grid-column: 1;
		grid-row: 1;
	}
	.menu{
		grid-column: 2;
		grid-row: 1;
	}
	.sesion{
		grid-column: 3;
		grid-row: 1;
	}
	.container {
		width: 65%;
	}
}
  
@media (min-width: 1200px) {
	header div.container {
		display: grid;
		grid-template-columns: 1fr 1fr 1fr;
		width: 100%;
	}
	header li{
		display: inline;
		margin: 10px;
	}
	.logo{
		grid-column: 1;
		grid-row: 1;
	}
	.menu{
		grid-column: 2;
		grid-row: 1;
		margin-top: 30px;
	}
	.sesion{
		grid-column: 3;
		grid-row: 1;
		margin-top: 30px;
	}
	.container {
		width: 65%;
	}
}

/**********
FOOTER
***********/
footer {
    font-size: 0.8rem;
	color: white;
	text-align: center;
}

footer a {
    color: rgb(255, 255, 255);
    border: none;
}

footer a:hover {
    color: #353d92;
}

div#autoria p,div#autoria a{
	font-size: 0.6rem;
	line-height: 0;
}

footer ul{
	margin: 30px;
	list-style: none;
}

div#autoria a#icono{
	font-size: 1.5rem;
	text-align: right;
  	display: inline-block;
	width: 95%;
}
  
@media (min-width: 992px) {
    footer li {
		display: inline;
	}
	
	footer ul li::before {
		content: "·";
		width: 10px;
		padding-right: 5px;
	}
	
	footer ul li:first-child::before {
		content: "";
	}
	div.footer {
		display: grid;
		grid-template-columns: 1fr 1fr;
	}
	div#autoria{
		text-align: left;
		margin-left: 35px;
	}
	div#autoria a#icono{
		text-align: right;
	}
	div.footer ul{
		text-align: right;
	}
	div.footer ul:first-child{
		text-align: left;
	}
}

@media (min-width: 1200px) {
    footer li {
		display: inline;
	}
	
	footer ul li::before {
		content: "·";
		width: 10px;
		padding-right: 5px;
	}
	
	footer ul li:first-child::before {
		content: "";
	}
	div.footer {
		display: grid;
		grid-template-columns: 1fr 1fr;
	}
	div#autoria{
		text-align: left;
		margin-left: 35px;
	}
	div#autoria a#icono{
		text-align: right;
	}
	div.footer ul{
		text-align: right;
	}
	div.footer ul:first-child{
		text-align: left;
	}
}

/* Index*/

.portada img{
	width: 40%;
	height: 40%;
}
.portada p{
	font-size: 1.5rem;
	line-height: 2;
}
div.portada{
	margin-top: 50px;
	margin-bottom: 50px;
}

div.imgportada img{
	max-width: 80%;
	display: block;
	margin: auto;
}
/* código para citar las imágenes o los recursos usados*/

.licencia{
	font-style: italic;
	text-align: right;
	margin-right: 10%;
	font-size: 0.6rem;
}

/* código para las frases inspirado en https://blog.artegrafico.net/creacion-de-un-slider-responsive-desde-cero-con-javascript-y-css */

.slider{
	position: relative;
	margin: auto;
}

.slider ul{
	list-style-type: none;
	margin: 0;
	padding: 0;
}

.slider ul li{
	position: relative;
	display: none;
	width: 100%;
	margin: auto;
}

.anterior, .siguiente {
	position: absolute;
	top: 45%;
	width: auto;
	margin-top: -22px;
	padding: 16px;
	color: #8cb2d3;
	font-weight: bold;
	font-size: 18px;
	transition: 0.6s ease;
	border-radius: 0 3px 3px 0;
	user-select: none;
  }

.anterior{
	left: -25%;
}
.siguiente {
	right: -25%;
	border-radius: 3px 0 0 3px;
  }
  
  /* color de fondo cuando estamos con el cursor sobre el elemento */
  .anterior:hover, .siguiente:hover {
	color: #ffffff;
	background-color: #353d92;
  }

  .slider ul li {
    animation-name: fade;
    animation-duration: 2.5s;
}
  
@keyframes fade {
    from {opacity: .2} 
    to {opacity: 1}
}

blockquote{
	margin: 80px 0px;
}

 div.slider img{
   width: 60%;
   height: 60%;
   display: block;
   margin-left: auto;
   margin-right: auto;
 }

 div.castelao {
  text-align: center;
  margin: 3% 10%;
 }

 div.castelao img{
   max-width: 80%;
   height: auto;
   margin: 0px auto;
 }
  
 .castelao div{

  margin: 0 15px;
 }

@media (min-width: 992px) {
    div.portada {
		display: flex;
		flex-direction: row;
	}
	.portada img{
		width: 70%;
		height: 70%;
	}
	.portada p{
		font-size: 1.2rem;
		line-height: 2;
	}
	h2.portada {
		margin-bottom: 0px;
		margin-top: 1.5rem;
	}
  
  div.castelao {
    display: inline-flex;
    margin: 3% 10%;
   }
  
   div.castelao img{
	width: auto;
	height: auto;
	margin: 0px auto;
   }
   div.imgportada img{
    margin-left: auto;
    margin-right: auto;
  }
    

}

@media (min-width: 1200px) {
    div.portada {
		display: flex;
		flex-direction: row;
	}
	.portada img{
		width: 70%;
		height: 70%;
	}
	.portada p{
		font-size: 1.2rem;
		line-height: 2;
	}
	h2.portada {
		margin-bottom: 0px;
		margin-top: 1.5rem;
	}
  div.castelao {
    display: inline-flex;
    margin: 3% 10%;
    text-align: left;
   }
  
   div.castelao img{
     width: 20%;
     height: auto;
     margin: 0px 5%;
   }
   
   div.imgportada img{
    margin-left: auto;
    margin-right: auto;
  }
}

/****Categorias ****/

div.container div.entradilla {
  text-align: left;
  text-decoration: none;
  margin-bottom: 30px;
}

article ul{
	list-style: none;
}

article{
	margin: 0;
	padding: 0;
}

article ul{
	text-align: left;
}

article ul li{
	margin-bottom: 30px;
}

article h3{
	text-align: left;
}

article img{
	width: 100%;
	max-height: 100%;
}


@media (min-width: 992px) {

	article li{
		max-width: 100%;
	}
}

@media (min-width: 1200px) {
	article li{
		max-width: 100%;
	}
	
}

@media (min-width: 992px) {

	div.contenido {
		display: grid;
		grid-template-columns: 1fr 3fr;
	}

	article ul {
		display: grid;
		grid-template-columns: 1fr 1fr 1fr;
		grid-gap: 20px;
	}
	.contenido article ul li {
		display: grid;
		grid-template-columns: 1fr;
		width: 90%;
	}
}

@media (min-width: 1200px) {

	div.contenido {
		display: grid;
		grid-template-columns: 1fr 3fr;
	}

	article ul {
		display: grid;
		grid-template-columns: 1fr 1fr 1fr;
	}
	.contenido article ul li {
		display: grid;
		grid-template-columns: 1fr;
		width: 90%;
	}
}

/****Página detalle***/

section.ficha img{
	width: auto;
	height: auto;
}

section.ficha{
	margin-top: 50px;
}

section h2{
	font-size: 1.5rem;
	text-align: left;
}

section p{
	text-align: left;
}

dl{
	text-align: left;
	margin-bottom: 20px;
	line-height: 1.8;
}
dt {
	font-weight: bold;
	border-bottom: 1px solid black;
	margin: 10px 0px;
}
dd {
	margin-left: 10px;
}
dl span{
	font-weight: bold;
}

.video-fluid {
    position: relative;
    display: block;
    width: 100%;
    padding: 0;
    overflow: hidden;
}

.video-fluid-item {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: 0;
}

.video-fluid::before {
    display: block;
    content: "";
    padding-top: 56.25%;
}

figcaption {
	margin-bottom: 20px;
}

div.discurso {
	text-align: center;
	background-color: rgb(204, 204, 204);
	padding: 10px;
	border-right: 2px solid rgb(55, 55, 55);;
	border-left: 2px solid rgb(55, 55, 55);
	margin-bottom: 30px;
}

div.discurso blockquote{
	padding: 10px;
}

div.discurso p{
	text-align: center;
}

div.discurso p span{
	font-weight: bold;
}


@media (min-width: 992px) {
	section.ficha {
		display: grid;
		grid-template-columns: 1fr 1fr;
		grid-gap: 50px;
	}

	section.ficha img{
		width: auto;
		height: auto;
		margin-bottom: 10px;
	}
}

@media (min-width: 1200px) {
	section.ficha {
		display: grid;
		grid-template-columns: 1fr 1fr;
		grid-gap: 50px;
	}

	section.ficha img{
		width: auto;
		height: auto;
		margin-bottom: 10px;
	}
}
