	position: relative;
	display: none;
	width: 100%;
	margin: auto;
}

.anterior, .siguiente {
	position: absolute;
	top: 45%;
	width: auto;
	margin-top: -22px;
	padding: 16px;
	color: #8cb2d3;
	font-weight: bold;
	font-size: 18px;
	transition: 0.6s ease;
	border-radius: 0 3px 3px 0;
	user-select: none;
  }

.anterior{
	left: -3%;
}
.siguiente {
	right: -3%;
	border-radius: 3px 0 0 3px;
  }
  
  /* color de fondo cuando estamos con el cursor sobre el elemento */
  #icono span{
	  color: white;
	  transition: 0.6s ease;
  }
  
  .anterior:hover, .siguiente:hover, #icono span:hover {
	color: #353d92;
	transform: scale(1.4);
  }

  .slider ul li {
    animation-name: fade;
    animation-duration: 2.5s;
}
  
@keyframes fade {
    from {opacity: .2} 
    to {opacity: 1}
}

